import React from "react";
import styles from "./ModalBox.module.css";
import Slideshow from "./Slideshow";

const ModalBox = ({ project, goToNext, goToPrevious }) => {
  const { infoList = [1], briefList = [2] } = project;
  return (
    <div>
      <h3>{project.id}</h3>
      <div className={styles.modalContainer}>
        <div className={styles.modalBody}>
          <button onClick={goToPrevious}>Previous</button>
          <button onClick={goToNext}>Next</button>
          <div className={styles.slide}>
            <Slideshow slides={project.slides} />
            <div className={styles.slideText}>{project.text}</div>
          </div>
        </div>
      </div>
      {/* project info */}
      <div className="swiper-pagination" />
      <div className={`${styles.rowModal} col-sm-12`}>
        <div className={`${styles.modalLeft} col-md-6`}>
          <ul className="modalBox-ul-info">
            {infoList.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul>
        </div>
        <div className={`${styles.modalRight} col-md-6`}>
          <ul className="modalBox-ul-brief">
            {briefList.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ModalBox;
