import React, { useState } from "react";
import styles from "./Education.module.css";

export default function Accordion({ label, content }) {
  const [isActive, setIsActive] = useState(false);
  const handleAccordionClick = () => {
    setIsActive(!isActive);
  };

  return (
    <div
      className={`${styles.contentBox} ${isActive ? styles.active : ""}`}
      onClick={handleAccordionClick}
    >
      <div className={styles.label}>{label}</div>
      <div className={styles.content}>
        <h5 style={{ textAlign: "center" }}>{content}</h5>
        <hr />
        <p className={styles.eduText}>text</p>
        <div className="row">
          <p style={{ textAlign: "center" }}>
            <strong>subtitle</strong>
          </p>
          <hr style={{ borderTop: "2px solid" }} />
          <div className={`${styles.eduContent} col-sm`}>
            <ul>
              <li>1</li>
            </ul>
          </div>
          <div className={`${styles.eduContent} col-sm`}>
            <ul>
              <li>2</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
