import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import styles from "./Navbar.module.css";
import "bootstrap/dist/css/bootstrap.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faTimes } from "@fortawesome/free-solid-svg-icons";

const Navbar = ({ setShowModal }) => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [click, setClick] = useState(false);
  const navbarRef = useRef(null);

  //change navbar color when scroll moves
  useEffect(() => {
    const handleHashChange = () => {
      setClick(false);
      if (navbarRef.current) {
        navbarRef.current.scrollIntoView({ behavior: "smooth" });
      }
    };

    window.addEventListener("hashchange", handleHashChange);

    return () => {
      window.removeEventListener("hashchange", handleHashChange);
    };
  }, []);

  const navbarStyle = {
    alignItems: "center",
    justifyContent: "space-between",
    backgroundColor: scrollPosition > 0 ? "black" : "transparent",
  };

  // navbar link
  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      const yOffset = -50;
      const y = element.offsetTop + yOffset;
      window.scrollTo({ top: y, behavior: "smooth" });
    }
  };

  // toggleBtn
  const handleClick = () => {
    setClick(!click);
  };

  const handleLinkClick = (id) => {
    setShowModal(false);
    scrollToSection(id);
  };

  return (
    <header id="header" className="fixed-top" style={navbarStyle}>
      <nav id="navbar" className={styles.navbar}>
        <Link
          className={styles.logo}
          to="/hero"
          onClick={() => scrollToSection("hero")}
        >
          Portfolio
        </Link>
        <ul className={`${styles.navbar_ul} ${click ? styles.active : ""}`}>
          <li className={styles.navbar_li}>
            <Link
              to="/hero"
              activeClassName="active"
              className="nav-link"
              onClick={() => scrollToSection("hero")}
            >
              Home
            </Link>
          </li>
          <li className={styles.navbar_li}>
            <Link
              to="/about"
              activeClassName="active"
              className="nav-link"
              onClick={() => handleLinkClick("about")}
            >
              About
            </Link>
          </li>
          <li className={styles.navbar_li}>
            <Link
              to="/skill"
              activeClassName="active"
              className="nav-link"
              onClick={() => scrollToSection("skill")}
            >
              Skills
            </Link>
          </li>
          <li className={styles.navbar_li}>
            <Link
              to="/edu"
              activeClassName="active"
              className="nav-link"
              onClick={() => scrollToSection("edu")}
            >
              Education
            </Link>
          </li>
          <li className={styles.navbar_li}>
            <Link
              to="/project"
              activeClassName="active"
              className="nav-link"
              onClick={() => scrollToSection("project")}
            >
              Project
            </Link>
          </li>
          <li className={styles.navbar_li}>
            <Link
              to="/contact"
              activeClassName="active"
              className="nav-link"
              onClick={() => scrollToSection("contact")}
            >
              Contact
            </Link>
          </li>
        </ul>

        {/* toggle bar for small screen size*/}
        <div
          className={`${styles.toggleBtn} ${click ? styles.active : ""}`}
          onClick={handleClick}
        >
          <FontAwesomeIcon icon={click ? faTimes : faBars} />
        </div>
      </nav>
    </header>
  );
};

export default Navbar;
