import React from "react";
import styles from "./CategoryBtn.module.css";

export default function CategoryBtn({ onSelectCategory }) {
  //const [selectedCategory, setSelectedCategory] = useState("all");

  const handleCategoryClick = (category) => {
    onSelectCategory(category);
  };

  return (
    <div className={styles.category}>
      <button id="btnAll" onClick={() => handleCategoryClick("all")}>
        All
      </button>
      <button id="btnAndroid" onClick={() => handleCategoryClick("java")}>
        Java
      </button>
      <button id="btnJs" onClick={() => handleCategoryClick("react")}>
        React
      </button>
      <button id="btnJs" onClick={() => handleCategoryClick("cSharp")}>
        C#
      </button>
    </div>
  );
}
