import React from "react";

export default function CategoryBtn({ onSelectCategory }) {
  const handleCategoryClick = (category) => {
    onSelectCategory(category);
  };

  const category = {
    display: "inline",
    textAlign: "center",
    margin: "0rem 0rem 2rem 0rem",
  };

  const categoryBtn = {
    padding: "0.4rem",
    marginLeft: "1rem",
    fontSize: "1rem",
    width: "20%",
    backgroundColor: "white",
    color: "black",
    border: "2px solid #e7e7e7",
    borderRadius: "10%",
  };

  const categoryBtnHover = {
    ...categoryBtn,
    backgroundColor: "#c8c4c3",
  };

  return (
    <div className="category" style={category}>
      <button
        id="btnAll"
        onClick={() => handleCategoryClick("all")}
        style={categoryBtn}
        onMouseOver={(e) => (e.target.style = categoryBtnHover)}
        onMouseOut={(e) => (e.target.style = categoryBtn)}
      >
        All
      </button>
      <button
        id="btnAndroid"
        onClick={() => handleCategoryClick("java")}
        style={categoryBtn}
        onMouseOver={(e) => (e.target.style = categoryBtnHover)}
        onMouseOut={(e) => (e.target.style = categoryBtn)}
      >
        Java
      </button>
      <button
        id="btnJs"
        onClick={() => handleCategoryClick("react")}
        style={categoryBtn}
        onMouseOver={(e) => (e.target.style = categoryBtnHover)}
        onMouseOut={(e) => (e.target.style = categoryBtn)}
      >
        React
      </button>
      <button
        id="btnJs"
        onClick={() => handleCategoryClick("cSharp")}
        style={categoryBtn}
        onMouseOver={(e) => (e.target.style = categoryBtnHover)}
        onMouseOut={(e) => (e.target.style = categoryBtn)}
      >
        C#
      </button>
    </div>
  );
}
