import React, { useState, useEffect } from "react";
import styles from "./ProjectContainer.module.css";
import { PlusCircle } from "react-bootstrap-icons";
import { ModalBoxContainer } from "./ModalBoxContainer";
import { useLocation } from "react-router-dom";

const ProjectBrief = ({ imgSrc, title, group, date, modalId }) => {
  const [isImageClicked, setIsImageClicked] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [selectedModal, setSelectedModal] = useState(null);
  const { pathname } = useLocation();

  const openModal = (modal) => {
    setSelectedModal(modal);
    setShowModal(true);
  };

  const handleImageClick = () => {
    setIsImageClicked(!isImageClicked);
  };

  useEffect(() => {
    if (pathname !== "/project") {
      setShowModal(false);
    }
  }, [pathname]);

  return (
    <div className="col-md-4">
      <div className={styles.projectBox}>
        <div className={styles.boxAndroid} id="android">
          <div
            className={`${styles.projectImg} ${
              isImageClicked ? styles.fullImage : ""
            }`}
          >
            <img
              src={imgSrc}
              alt="img"
              className="img-fluid"
              onClick={handleImageClick}
            />
          </div>
          <div className={styles.projectContent}>
            <div className="row">
              <div className="col-sm-8">
                <h2 className={styles.PTitle}>{title}</h2>
                <div className={styles.more}>
                  <span className={styles.group}>{group}</span> /{" "}
                  <span className={styles.Date}>{date}</span>
                </div>
              </div>
              <div className="col-sm-4">
                <div className={styles.btnBox}>
                  <button
                    onClick={() => openModal(modalId)}
                    className={styles.projectBtn}
                  >
                    <span>
                      <PlusCircle />
                    </span>
                  </button>
                  {showModal && (
                    <ModalBoxContainer
                      setShowModal={setShowModal}
                      selectedModal={selectedModal}
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectBrief;
